query:
{
  contentfulBio (
    name: {
      regex: "/identity/i"
    }
  )
  {
    name
    displayName
    headline
    photo {
      sizes {
        src
      }
    }
  }

  allContentfulSocialProfiles (
    filter: {
      name: {
        regex: "/(github|medium|twitter)/i"
      }
    }
  )
  {
    edges {
      node {
        name
        url
        className
      }
    }
  }


  bgPhoto: contentfulAsset (
    title: {
      regex: "/(nyc-chinatown-bw)/i"
    }
  )
  {
    title
    sizes(maxWidth: 1200, quality: 95) {
      src
    }
  }

  avatarPhoto: contentfulAsset (
    title: {
      regex: "/(steven)/i"
    }
  )
  {
    title
    sizes(maxWidth: 120, quality: 95) {
      src
    }
  }

}
